import { useState } from "react";
import { NavLink, Link } from "react-router-dom";
import { useIsMobile } from "../Core/hooks";

import {
  Navbar,
  NavbarBrand,
  NavbarToggler,
  Collapse,
  Nav,
  NavItem,
} from "reactstrap";

function Navigation() {
  const [isOpen, setIsOpen] = useState(false);
  const [hovered, setHovered] = useState<string>("");

  const isMobile = useIsMobile();

  const ACCENT = "#4CAF50";
  const TEXT = "#ffffff";

  const whiteHamburgerIcon = {
    backgroundImage:
      "url(\"data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 30 30'%3e%3cpath stroke='white' stroke-width='2' stroke-linecap='round' d='M4 7h22M4 15h22M4 23h22'/%3e%3c/svg%3e\")",
  };

  const items = [
    { to: "/solutions", label: "Solutions" },
    { to: "/materials", label: "Materials" },
    { to: "/about", label: "About" },
    { to: "/contact", label: "Contact" },
    { to: "/careers", label: "Careers" },
  ];

  return (
    <Navbar
      expand="xl"
      light
      className="px-3"
      style={{
        paddingTop: 0,
        paddingBottom: 0,
        backgroundColor: "transparent",
      }}
    >
      <NavbarBrand tag={Link} to="/" style={{ cursor: "pointer" }}>
        <img
          style={{ width: "75px", cursor: "pointer" }}
          src="/images/btr-green-arrow.gif"
          alt="BTR Polymers"
        />
      </NavbarBrand>

      <NavbarToggler onClick={() => setIsOpen((p) => !p)} className="ms-auto">
        <span className="navbar-toggler-icon" style={whiteHamburgerIcon} />
      </NavbarToggler>

      <Collapse isOpen={isOpen} navbar className="justify-content-end">
        <Nav navbar className="ms-auto">
          {items.map(({ to, label }) => (
            <NavItem key={to} style={{ display: "flex", alignItems: "stretch" }}>
              <NavLink
                to={to}
                onClick={() => setIsOpen(false)}
                onMouseEnter={() => setHovered(to)}
                onMouseLeave={() => setHovered("")}
                style={({ isActive }) => {
                  const on = isActive || hovered === to;

                  return {
                    // Hard override any bootstrap nav-link behavior
                    display: "inline-flex",
                    alignItems: "center",
                    justifyContent: "center",

                    // Make all items the same height so underline never shifts anything
                    height: "72px",
                    padding: "0 18px",

                    color: on ? ACCENT : TEXT,
                    textDecoration: "none",
                    fontWeight: 500,

                    // THIS is the key: reserve underline space ALWAYS
                    borderBottom: "2px solid transparent",
                    borderBottomColor: on ? ACCENT : "transparent",

                    // Prevent layout weirdness
                    boxSizing: "border-box",
                    background: "transparent",
                    outline: "none",

                    transition: "color 150ms ease, border-bottom-color 150ms ease",
                    cursor: "pointer",
                  };
                }}
              >
                {label}
              </NavLink>
            </NavItem>
          ))}
        </Nav>
      </Collapse>
    </Navbar>
  );
}

export default Navigation;
