import React from 'react';
import { useEffect, useState } from 'react';
import { Link, useNavigate, useSearchParams } from 'react-router-dom';
import { useAppSelector, useAppDispatch } from '../../Core/hooks';
import Footer from '../../Components/Footer';
import Navigation from '../../Components/Navigation';
import { useIsMobile } from '../../Core/hooks';
import { RxCalendar, RxLightningBolt } from 'react-icons/rx';
import { IoAnalytics, IoListSharp, IoPeople } from 'react-icons/io5';
import { MdAutoStories } from 'react-icons/md';
import { BiConversation } from 'react-icons/bi';

/*
interface Feedback {
    answers: Record<string, boolean>;
    otherText?: string;
}
*/
function Home() {

    const dispatch = useAppDispatch();
    const navigate = useNavigate();
    const [email, setEmail] = useState<string>("");
    const [firstName, setFirstName] = useState<string>("");
    const [lastName, setLastName] = useState<string>("");
    const [error, setError] = useState<boolean>(false);
    const [success, setSuccess] = useState<boolean>(false);
    const [feedback, setFeedback] = useState("");

    const [answers, setAnswers] = useState<Record<string, boolean>>({});
    const isMobile = useIsMobile();



    useEffect(() => {
        window.scrollTo(0, 0);
    }, []);


    const handleCheckboxChange = (point: string) => {
        setAnswers((prev) => ({
          ...prev,
          [point]: !prev[point],
        }));
      };

    return (
    <>
        <Navigation />

        <div className="main-container">
            {/* HERO */}
            <div
                style={{
                maxWidth: "1200px",
                margin: "0 auto",
                padding: isMobile ? "34px 18px 10px 18px" : "60px 24px 18px 24px",
                }}
            >
                <div
                style={{
                    display: "grid",
                    gridTemplateColumns: isMobile ? "1fr" : "1.05fr 0.95fr",
                    gap: isMobile ? "18px" : "26px",
                    alignItems: "center",
                }}
                >
                <div>
                    <div
                    style={{
                        display: "inline-flex",
                        alignItems: "center",
                        gap: "10px",
                        padding: "10px 14px",
                        borderRadius: "999px",
                        border: "1px solid rgba(255,255,255,0.14)",
                        background: "rgba(255,255,255,0.06)",
                        backdropFilter: "blur(10px)",
                        color: "rgba(255,255,255,0.78)",
                        fontSize: "13px",
                        width: "fit-content",
                    }}
                    >
                    <span
                        style={{
                        width: 10,
                        height: 10,
                        borderRadius: 999,
                        background: "#76CF61",
                        boxShadow: "0 0 0 6px rgba(118,207,97,0.12)",
                        }}
                    />
                    BTR Polymers • Post-industrial recycling • Reprocessed resins
                    </div>

                    <h1
                    style={{
                        fontWeight: 900,
                        fontSize: isMobile ? "42px" : "62px",
                        margin: "16px 0 12px 0",
                        letterSpacing: "-1px",
                        lineHeight: 1.02,
                    }}
                    >
                    Turning Industrial Plastic Waste into High-Performance Materials
                    </h1>

                    <div
                    style={{
                        color: "rgba(255,255,255,0.74)",
                        fontSize: isMobile ? "16px" : "18px",
                        lineHeight: 1.65,
                        maxWidth: "760px",
                        marginBottom: "22px",
                    }}
                    >
                    <strong style={{ color: "rgba(255,255,255,0.92)" }}>BTR Polymers</strong> is a service-driven
                    materials company specializing in the recycling and reprocessing of post-industrial polymer
                    materials. We provide sustainable resin solutions to plastics manufacturers around the world—helping
                    businesses reduce waste, control material costs, and strengthen their supply chains.
                    </div>

                    <div style={{ display: "flex", gap: "12px", flexWrap: "wrap", alignItems: "center" }}>
                    <div
                        onClick={() => navigate("/contact")}
                        style={{
                        display: "inline-flex",
                        alignItems: "center",
                        justifyContent: "center",
                        padding: "14px 18px",
                        borderRadius: "999px",
                        cursor: "pointer",
                        border: "1px solid rgba(118,207,97,0.35)",
                        background: "linear-gradient(135deg, rgba(118,207,97,0.22), rgba(52,211,153,0.14))",
                        color: "rgba(255,255,255,0.92)",
                        fontWeight: 750,
                        textDecoration: "none",
                        boxShadow: "0 18px 40px rgba(0,0,0,0.35)",
                        }}
                    >
                        Contact us <span style={{ color: "#76CF61", marginLeft: 8 }}>→</span>
                    </div>

                    <div
                        onClick={() => navigate("/solutions")}
                        style={{
                        display: "inline-flex",
                        alignItems: "center",
                        justifyContent: "center",
                        padding: "14px 18px",
                        borderRadius: "999px",
                        cursor: "pointer",
                        border: "1px solid rgba(255,255,255,0.16)",
                        background: "rgba(255,255,255,0.06)",
                        color: "rgba(255,255,255,0.90)",
                        fontWeight: 700,
                        }}
                    >
                        View Solutions
                    </div>

                    <a
                        href="#what-we-do"
                        style={{
                        color: "rgba(255,255,255,0.86)",
                        textDecoration: "none",
                        borderBottom: "1px solid rgba(255,255,255,0.28)",
                        paddingBottom: 2,
                        fontWeight: 650,
                        }}
                    >
                        What we do
                    </a>
                    </div>

                    <div
                    style={{
                        marginTop: 18,
                        color: "rgba(255,255,255,0.66)",
                        lineHeight: 1.7,
                        maxWidth: "900px",
                    }}
                    >
                    From boutique molders to global producers, BTR partners with companies of all sizes to transform
                    plastic scrap into valuable resources. By connecting the circular economy to real-world manufacturing
                    needs, we deliver practical solutions that work for everyday businesses.
                    </div>
                </div>

                {/* HERO IMAGE CARD */}
                <div
                    style={{
                    borderRadius: "22px",
                    border: "1px solid rgba(255,255,255,0.12)",
                    background: "rgba(255,255,255,0.06)",
                    overflow: "hidden",
                    position: "relative",
                    boxShadow: "0 24px 60px rgba(0,0,0,0.35)",
                    minHeight: isMobile ? "280px" : "420px",
                    }}
                >
                    <img
                    src="/images/btr-hero-recycling.jpg"
                    alt="Recycling and reprocessing polymers"
                    style={{ width: "100%", height: "100%", objectFit: "cover", display: "block" }}
                    onError={(e) => {
                        (e.currentTarget as HTMLImageElement).src = "/images/recycled-pellets.jpg";
                    }}
                    />
                    <div
                    style={{
                        position: "absolute",
                        inset: 0,
                        background:
                        "linear-gradient(180deg, rgba(6,27,42,0.20) 0%, rgba(6,27,42,0.92) 70%, rgba(4,20,33,0.96) 100%)",
                    }}
                    />
                    <div
                    style={{
                        position: "absolute",
                        left: 16,
                        right: 16,
                        bottom: 16,
                        padding: 16,
                        borderRadius: 18,
                        border: "1px solid rgba(255,255,255,0.10)",
                        background: "rgba(0,0,0,0.22)",
                        backdropFilter: "blur(10px)",
                    }}
                    >
                    <div style={{ fontWeight: 850, fontSize: 16, marginBottom: 6 }}>
                        Sustainable Materials. Reliable Supply. Real Results.
                    </div>
                    <div style={{ color: "rgba(255,255,255,0.74)", lineHeight: 1.55, fontSize: 14 }}>
                        Sustainability isn’t a slogan—it’s a strategy. We source, process, and supply recycled polymers
                        that meet modern manufacturing performance demands.
                    </div>

                    <div
                        style={{
                        display: "grid",
                        gridTemplateColumns: "repeat(3, minmax(0, 1fr))",
                        gap: 10,
                        marginTop: 12,
                        }}
                    >
                        {[
                        { k: "Focus", v: "Post-Industrial" },
                        { k: "Value", v: "Cost Control" },
                        { k: "Outcome", v: "Supply Stability" },
                        ].map((s) => (
                        <div
                            key={s.k}
                            style={{
                            borderRadius: 14,
                            border: "1px solid rgba(255,255,255,0.10)",
                            background: "rgba(255,255,255,0.06)",
                            padding: "10px 10px",
                            }}
                        >
                            <div style={{ fontSize: 12, color: "rgba(255,255,255,0.70)" }}>{s.k}</div>
                            <div style={{ fontWeight: 850, marginTop: 4 }}>{s.v}</div>
                        </div>
                        ))}
                    </div>
                    </div>
                </div>
                </div>
            </div>

            {/* DIVIDER */}
            <div style={{ height: 1, background: "rgba(255,255,255,0.10)", maxWidth: 1200, margin: "0 auto" }} />

            {/* WHAT WE DO */}
            <div id="what-we-do" style={{ maxWidth: 1200, margin: "0 auto", padding: isMobile ? "34px 18px" : "54px 24px" }}>
                <div style={{ display: "flex", justifyContent: "space-between", gap: 16, flexWrap: "wrap" }}>
                <div>
                    <div style={{ fontSize: 28, fontWeight: 900, letterSpacing: "-0.5px", marginBottom: 8 }}>What We Do</div>
                    <div style={{ color: "rgba(255,255,255,0.70)", lineHeight: 1.7, maxWidth: 900 }}>
                    We specialize in recycling and reprocessing post-industrial plastics, supplying recycled and
                    reprocessed polymer resins, and building closed-loop programs that keep plastics in productive use.
                    </div>
                </div>

                <div
                    style={{
                    display: "inline-flex",
                    alignItems: "center",
                    gap: 10,
                    padding: "10px 14px",
                    borderRadius: 999,
                    border: "1px solid rgba(255,255,255,0.14)",
                    background: "rgba(255,255,255,0.06)",
                    height: "fit-content",
                    color: "rgba(255,255,255,0.78)",
                    fontSize: 13,
                    }}
                >
                    <span style={{ color: "#76CF61", fontWeight: 900 }}>BTR</span> • service-driven execution
                </div>
                </div>

                <div
                style={{
                    marginTop: 18,
                    display: "grid",
                    gridTemplateColumns: isMobile ? "1fr" : "repeat(3, minmax(0, 1fr))",
                    gap: 14,
                }}
                >
                {[
                    {
                    title: "Recycle & Reprocess",
                    desc: "Recycling and reprocessing post-industrial plastics into usable resin streams.",
                    n: "01",
                    },
                    {
                    title: "Resin Supply",
                    desc: "Supplying recycled and reprocessed polymer resins with consistent specs.",
                    n: "02",
                    },
                    {
                    title: "Closed-Loop Programs",
                    desc: "Creating closed-loop programs for manufacturers—scrap becomes supply advantage.",
                    n: "03",
                    },
                    {
                    title: "Sustainable Sourcing",
                    desc: "Supporting sustainable sourcing initiatives with dependable, scalable materials.",
                    n: "04",
                    },
                    {
                    title: "Supply Chain Strength",
                    desc: "Strengthening domestic and global polymer supply chains without compromising performance.",
                    n: "05",
                    },
                    {
                    title: "Programs Built for Results",
                    desc: "Every program is designed to deliver high-quality materials while keeping plastics in productive use.",
                    n: "06",
                    },
                ].map((x) => (
                    <div
                    key={x.title}
                    style={{
                        borderRadius: 20,
                        border: "1px solid rgba(255,255,255,0.12)",
                        background: "rgba(255,255,255,0.06)",
                        padding: 18,
                    }}
                    >
                    <div style={{ display: "flex", gap: 12, alignItems: "center", marginBottom: 10 }}>
                        <div
                        style={{
                            width: 38,
                            height: 38,
                            borderRadius: 12,
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            background: "rgba(118,207,97,0.12)",
                            border: "1px solid rgba(118,207,97,0.22)",
                            fontWeight: 900,
                        }}
                        >
                        {x.n}
                        </div>
                        <div style={{ fontWeight: 900, fontSize: 18 }}>{x.title}</div>
                    </div>
                    <div style={{ color: "rgba(255,255,255,0.70)", lineHeight: 1.65 }}>{x.desc}</div>
                    </div>
                ))}
                </div>
            </div>

            {/* IMAGE STRIP */}
            <div style={{ maxWidth: 1200, margin: "0 auto", padding: isMobile ? "0 18px 34px 18px" : "0 24px 54px 24px" }}>
                <div
                style={{
                    display: "grid",
                    gridTemplateColumns: isMobile ? "1fr" : "repeat(3, minmax(0, 1fr))",
                    gap: 14,
                }}
                >
                {[
                    { src: "/images/btr-bales.jpg", alt: "Post-industrial plastic bales" },
                    { src: "/images/btr-pellets.jpg", alt: "Reprocessed polymer pellets" },
                    { src: "/images/btr-warehouse.jpg", alt: "Warehouse and supply logistics" },
                ].map((img) => (
                    <div
                    key={img.src}
                    style={{
                        borderRadius: 20,
                        overflow: "hidden",
                        border: "1px solid rgba(255,255,255,0.12)",
                        background: "rgba(255,255,255,0.06)",
                        minHeight: 200,
                    }}
                    >
                    <img
                        src={img.src}
                        alt={img.alt}
                        style={{ width: "100%", height: 220, objectFit: "cover", display: "block" }}
                        onError={(e) => {
                        (e.currentTarget as HTMLImageElement).src = "/images/recycled-pellets.jpg";
                        }}
                    />
                    </div>
                ))}
                </div>
            </div>

            {/* PARTNERS SECTION */}
            <div style={{ maxWidth: 1200, margin: "0 auto", padding: isMobile ? "34px 18px" : "54px 24px" }}>
                <div
                style={{
                    display: "grid",
                    gridTemplateColumns: isMobile ? "1fr" : "1fr 1fr",
                    gap: 16,
                    alignItems: "stretch",
                }}
                >
                <div
                    style={{
                    borderRadius: 22,
                    border: "1px solid rgba(255,255,255,0.12)",
                    background: "rgba(255,255,255,0.06)",
                    padding: 18,
                    }}
                >
                    <div style={{ fontSize: 26, fontWeight: 900, letterSpacing: "-0.5px", marginBottom: 10 }}>
                    Partners in the Circular Economy
                    </div>
                    <div style={{ color: "rgba(255,255,255,0.70)", lineHeight: 1.7 }}>
                    BTR Polymers works side-by-side with manufacturers to create smarter material strategies. Whether
                    reclaiming in-house scrap, sourcing recycled resin, or building custom recycling programs, we help
                    companies close the loop without compromising performance or profitability.
                    </div>

                    <div style={{ marginTop: 14 }}>
                    {[
                        "Reclaim in-house scrap streams",
                        "Source recycled resin reliably",
                        "Build custom closed-loop programs",
                        "Improve sustainability without margin pain",
                    ].map((t) => (
                        <div
                        key={t}
                        style={{
                            display: "flex",
                            gap: 12,
                            alignItems: "flex-start",
                            padding: "10px 0",
                            borderBottom: "1px solid rgba(255,255,255,0.08)",
                        }}
                        >
                        <div
                            style={{
                            width: 10,
                            height: 10,
                            borderRadius: 999,
                            background: "#76CF61",
                            boxShadow: "0 0 0 6px rgba(118,207,97,0.12)",
                            marginTop: 6,
                            flex: "0 0 auto",
                            }}
                        />
                        <div style={{ color: "rgba(255,255,255,0.82)", lineHeight: 1.6 }}>{t}</div>
                        </div>
                    ))}
                    </div>

                    <div style={{ marginTop: 16, display: "flex", gap: 12, flexWrap: "wrap" }}>
                    <div
                        onClick={() => navigate("/materials")}
                        style={{
                        display: "inline-flex",
                        alignItems: "center",
                        justifyContent: "center",
                        padding: "12px 16px",
                        borderRadius: 999,
                        cursor: "pointer",
                        border: "1px solid rgba(255,255,255,0.16)",
                        background: "rgba(255,255,255,0.06)",
                        color: "rgba(255,255,255,0.90)",
                        fontWeight: 750,
                        }}
                    >
                        Explore Materials
                    </div>
                    <div
                        onClick={() => navigate("/solutions")}
                        style={{
                        display: "inline-flex",
                        alignItems: "center",
                        justifyContent: "center",
                        padding: "12px 16px",
                        borderRadius: 999,
                        cursor: "pointer",
                        border: "1px solid rgba(118,207,97,0.35)",
                        background: "linear-gradient(135deg, rgba(118,207,97,0.22), rgba(52,211,153,0.14))",
                        color: "rgba(255,255,255,0.92)",
                        fontWeight: 800,
                        }}
                    >
                        See Capabilities →
                    </div>
                    </div>
                </div>

                <div
                    style={{
                    borderRadius: 22,
                    border: "1px solid rgba(255,255,255,0.12)",
                    background:
                        "radial-gradient(900px 260px at 20% 30%, rgba(118,207,97,0.20), transparent 55%), rgba(255,255,255,0.06)",
                    padding: 18,
                    }}
                >
                    <div style={{ fontSize: 26, fontWeight: 900, letterSpacing: "-0.5px", marginBottom: 10 }}>
                    Sustainable Materials. Reliable Supply. Real Results.
                    </div>
                    <div style={{ color: "rgba(255,255,255,0.70)", lineHeight: 1.7 }}>
                    At BTR Polymers, sustainability is not a slogan—it’s a strategy. We source, process, and supply
                    recycled polymers that meet the performance demands of modern manufacturing while advancing
                    environmental responsibility.
                    </div>

                    <div style={{ marginTop: 14, color: "rgba(255,255,255,0.74)", lineHeight: 1.7 }}>
                    Our team combines technical expertise with hands-on market knowledge to ensure customers receive
                    consistent, dependable materials tailored to their production requirements.
                    </div>

                    <div
                    style={{
                        marginTop: 16,
                        display: "grid",
                        gridTemplateColumns: "repeat(2, minmax(0, 1fr))",
                        gap: 12,
                    }}
                    >
                    {[
                        { k: "Program Type", v: "Closed-loop & supply" },
                        { k: "Material Focus", v: "Post-industrial polymers" },
                        { k: "Customer Fit", v: "Molders → global producers" },
                        { k: "Outcome", v: "Cost + reliability" },
                    ].map((s) => (
                        <div
                        key={s.k}
                        style={{
                            borderRadius: 16,
                            border: "1px solid rgba(255,255,255,0.10)",
                            background: "rgba(255,255,255,0.06)",
                            padding: 12,
                        }}
                        >
                        <div style={{ fontSize: 12, color: "rgba(255,255,255,0.66)" }}>{s.k}</div>
                        <div style={{ fontWeight: 900, marginTop: 4 }}>{s.v}</div>
                        </div>
                    ))}
                    </div>

                    <div style={{ marginTop: 18 }}>
                    <div
                        onClick={() => navigate("/contact")}
                        style={{
                        display: "inline-flex",
                        alignItems: "center",
                        justifyContent: "center",
                        padding: "14px 18px",
                        borderRadius: 999,
                        cursor: "pointer",
                        border: "1px solid rgba(118,207,97,0.35)",
                        background: "linear-gradient(135deg, rgba(118,207,97,0.22), rgba(52,211,153,0.14))",
                        color: "rgba(255,255,255,0.92)",
                        fontWeight: 850,
                        }}
                    >
                        Let’s build a more sustainable supply chain →
                    </div>
                    </div>
                </div>
                </div>
            </div>
            </div>


        <Footer style={{ position: "relative" }} />
    </>
    );
}

