import { useState } from 'react';
import { NavLink, Link, useNavigate } from 'react-router-dom';
import { useIsMobile } from '../Core/hooks';

import {
  Navbar,
  NavbarBrand,
  NavbarToggler,
  Collapse,
  Nav,
  NavItem
} from "reactstrap";

function Navigation() {
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();
  const isMobile = useIsMobile();

  const ACCENT = "#4CAF50";
  const TEXT = "#ffffff";

  const toggle = () => setIsOpen(prev => !prev);

  const navLinkStyle = ({ isActive }: { isActive: boolean }) => ({
    color: isActive ? ACCENT : TEXT,
    padding: "0.75rem 1rem",
    textDecoration: "none",
    borderBottom: `2px solid ${isActive ? ACCENT : "transparent"}`,
    transition: "color 0.2s ease, border-bottom-color 0.2s ease",
    display: "inline-block"
  });

  return (
    <Navbar expand="xl" light className="px-3" style={{ paddingTop: 0, paddingBottom: 0 }}>
      {/* LOGO */}
      <NavbarBrand tag={Link} to="/" style={{ cursor: "pointer" }}>
        <img
          src="/images/btr-green-arrow.gif"
          alt="BTR"
          style={{ width: "75px" }}
        />
      </NavbarBrand>

      {/* HAMBURGER */}
      <NavbarToggler onClick={toggle} className="ms-auto">
        <span
          className="navbar-toggler-icon"
          style={{
            backgroundImage:
              "url(\"data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 30 30'%3e%3cpath stroke='white' stroke-width='2' stroke-linecap='round' d='M4 7h22M4 15h22M4 23h22'/%3e%3c/svg%3e\")"
          }}
        />
      </NavbarToggler>

      {/* NAV */}
      <Collapse isOpen={isOpen} navbar className="justify-content-end">
        <Nav navbar className="ms-auto">
          {[
            { to: "/solutions", label: "Solutions" },
            { to: "/materials", label: "Materials" },
            { to: "/about", label: "About" },
            { to: "/contact", label: "Contact" },
            { to: "/careers", label: "Careers" }
          ].map(({ to, label }) => (
            <NavItem key={to}>
              <NavLink
                to={to}
                onClick={() => setIsOpen(false)}
                style={navLinkStyle}
                onMouseEnter={e => {
                  e.currentTarget.style.color = ACCENT;
                  e.currentTarget.style.borderBottomColor = ACCENT;
                }}
                onMouseLeave={e => {
                  if (!e.currentTarget.getAttribute("aria-current")) {
                    e.currentTarget.style.color = TEXT;
                    e.currentTarget.style.borderBottomColor = "transparent";
                  }
                }}
              >
                {label}
              </NavLink>
            </NavItem>
          ))}
        </Nav>
      </Collapse>
    </Navbar>
  );
}

export default Navigation;
