// src/Pages/Home/Home.tsx
import React from "react";
import { Link } from "react-router-dom";

export default function Home() {
  const bg = "#061b2a";
  const bg2 = "#041421";
  const text = "rgba(255,255,255,0.92)";
  const muted = "rgba(255,255,255,0.72)";
  const accent = "#76CF61";
  const accent2 = "#34D399";

  const page: React.CSSProperties = {
    background: `radial-gradient(1200px 600px at 20% -10%, rgba(118,207,97,0.18), transparent 60%),
                 radial-gradient(900px 500px at 85% 10%, rgba(52,211,153,0.14), transparent 55%),
                 linear-gradient(180deg, ${bg} 0%, ${bg2} 100%)`,
    color: text,
  };

  const container: React.CSSProperties = {
    maxWidth: 1200,
    margin: "0 auto",
    padding: "0 24px",
  };

  const pill: React.CSSProperties = {
    display: "inline-flex",
    alignItems: "center",
    gap: 10,
    padding: "10px 14px",
    borderRadius: 999,
    border: "1px solid rgba(255,255,255,0.14)",
    background: "rgba(255,255,255,0.06)",
    backdropFilter: "blur(10px)",
    fontSize: 13,
    color: muted,
  };

  const h1: React.CSSProperties = {
    fontSize: "clamp(40px, 5vw, 62px)",
    lineHeight: 1.02,
    letterSpacing: "-0.02em",
    margin: "14px 0 12px 0",
    color: "rgba(255,255,255,0.96)",
    fontWeight: 800,
  };

  const sub: React.CSSProperties = {
    fontSize: "clamp(16px, 2.0vw, 18px)",
    lineHeight: 1.6,
    color: muted,
    maxWidth: 760,
    marginBottom: 26,
  };

  const ctaRow: React.CSSProperties = {
    display: "flex",
    gap: 14,
    flexWrap: "wrap",
    alignItems: "center",
  };

  const primaryBtn: React.CSSProperties = {
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    gap: 10,
    padding: "14px 18px",
    borderRadius: 999,
    border: "1px solid rgba(118,207,97,0.35)",
    background: `linear-gradient(135deg, rgba(118,207,97,0.22), rgba(52,211,153,0.14))`,
    color: "rgba(255,255,255,0.92)",
    textDecoration: "none",
    fontWeight: 700,
    letterSpacing: "0.01em",
    boxShadow: "0 18px 40px rgba(0,0,0,0.35)",
  };

  const secondaryBtn: React.CSSProperties = {
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "14px 18px",
    borderRadius: 999,
    border: "1px solid rgba(255,255,255,0.16)",
    background: "rgba(255,255,255,0.06)",
    color: "rgba(255,255,255,0.90)",
    textDecoration: "none",
    fontWeight: 650,
  };

  const heroGrid: React.CSSProperties = {
    display: "grid",
    gridTemplateColumns: "1.1fr 0.9fr",
    gap: 26,
    padding: "72px 0 34px 0",
    alignItems: "center",
  };

  const heroCard: React.CSSProperties = {
    borderRadius: 22,
    border: "1px solid rgba(255,255,255,0.12)",
    background: "rgba(255,255,255,0.06)",
    boxShadow: "0 24px 60px rgba(0,0,0,0.35)",
    overflow: "hidden",
    position: "relative",
  };

  const heroImg: React.CSSProperties = {
    width: "100%",
    height: 380,
    objectFit: "cover",
    display: "block",
    filter: "saturate(1.05) contrast(1.05)",
  };

  const heroOverlay: React.CSSProperties = {
    position: "absolute",
    inset: 0,
    background:
      "linear-gradient(180deg, rgba(6,27,42,0.25) 0%, rgba(6,27,42,0.92) 70%, rgba(4,20,33,0.96) 100%)",
  };

  const heroCardInner: React.CSSProperties = {
    position: "absolute",
    left: 18,
    right: 18,
    bottom: 18,
    padding: 18,
    borderRadius: 18,
    border: "1px solid rgba(255,255,255,0.10)",
    background: "rgba(0,0,0,0.22)",
    backdropFilter: "blur(10px)",
  };

  const statRow: React.CSSProperties = {
    display: "grid",
    gridTemplateColumns: "repeat(3, minmax(0, 1fr))",
    gap: 12,
    marginTop: 14,
  };

  const stat: React.CSSProperties = {
    borderRadius: 16,
    border: "1px solid rgba(255,255,255,0.10)",
    background: "rgba(255,255,255,0.06)",
    padding: "12px 12px",
  };

  const section: React.CSSProperties = {
    padding: "54px 0",
  };

  const sectionTitle: React.CSSProperties = {
    fontSize: 28,
    fontWeight: 800,
    letterSpacing: "-0.02em",
    margin: "0 0 10px 0",
    color: "rgba(255,255,255,0.96)",
  };

  const sectionLead: React.CSSProperties = {
    color: muted,
    lineHeight: 1.7,
    maxWidth: 900,
    margin: "0 0 22px 0",
  };

  const cards: React.CSSProperties = {
    display: "grid",
    gridTemplateColumns: "repeat(3, minmax(0, 1fr))",
    gap: 14,
  };

  const card: React.CSSProperties = {
    borderRadius: 20,
    border: "1px solid rgba(255,255,255,0.12)",
    background: "rgba(255,255,255,0.06)",
    padding: 18,
  };

  const kbd: React.CSSProperties = {
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    width: 36,
    height: 36,
    borderRadius: 12,
    background: "rgba(118,207,97,0.12)",
    border: "1px solid rgba(118,207,97,0.22)",
    color: "rgba(255,255,255,0.92)",
    fontWeight: 800,
  };

  const split: React.CSSProperties = {
    display: "grid",
    gridTemplateColumns: "1fr 1fr",
    gap: 18,
    alignItems: "stretch",
  };

  const mediaCard: React.CSSProperties = {
    ...card,
    padding: 0,
    overflow: "hidden",
  };

  const mediaImg: React.CSSProperties = {
    width: "100%",
    height: 260,
    objectFit: "cover",
    display: "block",
  };

  const mediaBody: React.CSSProperties = {
    padding: 18,
  };

  const bullet: React.CSSProperties = {
    display: "flex",
    gap: 12,
    alignItems: "flex-start",
    padding: "10px 0",
    borderBottom: "1px solid rgba(255,255,255,0.08)",
  };

  const dot: React.CSSProperties = {
    width: 10,
    height: 10,
    borderRadius: 999,
    background: accent,
    boxShadow: "0 0 0 6px rgba(118,207,97,0.12)",
    marginTop: 6,
    flex: "0 0 auto",
  };

  const ctaPanel: React.CSSProperties = {
    borderRadius: 24,
    border: "1px solid rgba(255,255,255,0.14)",
    background:
      "radial-gradient(900px 260px at 20% 30%, rgba(118,207,97,0.20), transparent 55%), rgba(255,255,255,0.06)",
    padding: 22,
    display: "grid",
    gridTemplateColumns: "1.2fr 0.8fr",
    gap: 18,
    alignItems: "center",
  };

  const smallLink: React.CSSProperties = {
    color: "rgba(255,255,255,0.88)",
    textDecoration: "none",
    borderBottom: "1px solid rgba(255,255,255,0.26)",
    paddingBottom: 2,
  };

  const hr: React.CSSProperties = {
    height: 1,
    border: 0,
    background: "rgba(255,255,255,0.10)",
    margin: "0",
  };

  // --- responsive (inline) ---
  const isNarrow =
    typeof window !== "undefined" ? window.matchMedia("(max-width: 992px)").matches : false;

  const heroGridResponsive: React.CSSProperties = isNarrow
    ? { ...heroGrid, gridTemplateColumns: "1fr", padding: "44px 0 22px 0" }
    : heroGrid;

  const cardsResponsive: React.CSSProperties = isNarrow
    ? { ...cards, gridTemplateColumns: "1fr" }
    : cards;

  const splitResponsive: React.CSSProperties = isNarrow
    ? { ...split, gridTemplateColumns: "1fr" }
    : split;

  const ctaPanelResponsive: React.CSSProperties = isNarrow
    ? { ...ctaPanel, gridTemplateColumns: "1fr" }
    : ctaPanel;

  return (
    <div style={page}>
      {/* HERO */}
      <div style={container}>
        <div style={heroGridResponsive}>
          <div>
            <div style={pill}>
              <span
                style={{
                  width: 10,
                  height: 10,
                  borderRadius: 999,
                  background: accent2,
                  boxShadow: "0 0 0 6px rgba(52,211,153,0.12)",
                }}
              />
              Service-driven recycled polymers • Post-industrial reprocessing • Supply chain strength
            </div>

            <h1 style={h1}>Turning Industrial Plastic Waste into High-Performance Materials</h1>

            <div style={sub}>
              <strong style={{ color: "rgba(255,255,255,0.92)" }}>BTR Polymers</strong> is a service-driven
              materials company specializing in the recycling and reprocessing of post-industrial polymer
              materials. We provide sustainable resin solutions to plastics manufacturers around the world—
              helping businesses reduce waste, control material costs, and strengthen their supply chains.
            </div>

            <div style={ctaRow}>
              <Link to="/contact" style={primaryBtn}>
                Let’s talk materials <span style={{ color: accent }}>→</span>
              </Link>
              <Link to="/solutions" style={secondaryBtn}>
                View Solutions
              </Link>
              <a href="#what-we-do" style={smallLink}>
                What we do
              </a>
            </div>

            <div style={{ marginTop: 18, color: muted, lineHeight: 1.7, maxWidth: 900 }}>
              From boutique molders to global producers, BTR partners with companies of all sizes to transform
              plastic scrap into valuable resources. By connecting the circular economy to real-world
              manufacturing needs, we deliver practical solutions that work for everyday businesses.
            </div>
          </div>

          {/* HERO MEDIA CARD */}
          <div style={heroCard}>
            {/* Replace with your real image path(s) */}
            <img
              src="/images/btr-hero-recycling.jpg"
              alt="Recycled polymers and industrial recycling"
              style={heroImg}
              onError={(e) => {
                // fallback to your existing brand asset if the image isn't there yet
                (e.currentTarget as HTMLImageElement).src = "/images/btr-green-arrow.gif";
                (e.currentTarget as HTMLImageElement).style.objectFit = "contain";
                (e.currentTarget as HTMLImageElement).style.padding = "70px";
              }}
            />
            <div style={heroOverlay} />
            <div style={heroCardInner}>
              <div style={{ fontWeight: 800, fontSize: 16, marginBottom: 6 }}>
                Sustainable Materials. Reliable Supply. Real Results.
              </div>
              <div style={{ color: "rgba(255,255,255,0.78)", lineHeight: 1.55, fontSize: 14 }}>
                Sustainability isn’t a slogan—it’s a strategy. We deliver consistent, dependable recycled
                polymers tailored to your production requirements.
              </div>

              <div style={statRow}>
                <div style={stat}>
                  <div style={{ fontSize: 12, color: "rgba(255,255,255,0.70)" }}>Focus</div>
                  <div style={{ fontWeight: 800, marginTop: 4 }}>Post-Industrial</div>
                </div>
                <div style={stat}>
                  <div style={{ fontSize: 12, color: "rgba(255,255,255,0.70)" }}>Value</div>
                  <div style={{ fontWeight: 800, marginTop: 4 }}>Cost Control</div>
                </div>
                <div style={stat}>
                  <div style={{ fontSize: 12, color: "rgba(255,255,255,0.70)" }}>Outcome</div>
                  <div style={{ fontWeight: 800, marginTop: 4 }}>Supply Stability</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <hr style={hr} />

      {/* WHAT WE DO */}
      <div id="what-we-do" style={section}>
        <div style={container}>
          <div style={{ display: "flex", justifyContent: "space-between", gap: 16, flexWrap: "wrap" }}>
            <div>
              <div style={sectionTitle}>What We Do</div>
              <div style={sectionLead}>
                Every program is designed with one goal in mind: deliver high-quality materials while keeping
                plastics in productive use.
              </div>
            </div>

            <div style={{ ...pill, height: "fit-content" }}>
              <span style={{ color: accent, fontWeight: 800 }}>BTR</span> • practical circular-economy execution
            </div>
          </div>

          <div style={cardsResponsive}>
            {[
              {
                title: "Recycle & Reprocess",
                desc: "Recycling and reprocessing post-industrial plastics into usable resin streams.",
                icon: "01",
              },
              {
                title: "Resin Supply",
                desc: "Supplying recycled and reprocessed polymer resins with consistent specs.",
                icon: "02",
              },
              {
                title: "Closed-Loop Programs",
                desc: "Creating closed-loop programs so your scrap becomes your supply advantage.",
                icon: "03",
              },
              {
                title: "Sustainable Sourcing",
                desc: "Supporting sustainable sourcing initiatives with dependable, scalable materials.",
                icon: "04",
              },
              {
                title: "Supply Chain Strength",
                desc: "Strengthening domestic and global polymer supply chains—without compromising performance.",
                icon: "05",
              },
              {
                title: "Service-Driven Execution",
                desc: "Hands-on market knowledge plus technical expertise—built for everyday manufacturing realities.",
                icon: "06",
              },
            ].map((x) => (
              <div key={x.title} style={card}>
                <div style={{ display: "flex", gap: 12, alignItems: "center", marginBottom: 10 }}>
                  <div style={kbd}>{x.icon}</div>
                  <div style={{ fontWeight: 850, fontSize: 18 }}>{x.title}</div>
                </div>
                <div style={{ color: muted, lineHeight: 1.65 }}>{x.desc}</div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <hr style={hr} />

      {/* PARTNERS / STORY + IMAGES */}
      <div style={section}>
        <div style={container}>
          <div style={splitResponsive}>
            <div style={mediaCard}>
              <img
                src="/images/btr-bales.jpg"
                alt="Post-industrial plastic bales"
                style={mediaImg}
                onError={(e) => {
                  (e.currentTarget as HTMLImageElement).src = "/images/btr-hero-recycling.jpg";
                }}
              />
              <div style={mediaBody}>
                <div style={{ fontWeight: 850, fontSize: 20, marginBottom: 8 }}>
                  Partners in the Circular Economy
                </div>
                <div style={{ color: muted, lineHeight: 1.7 }}>
                  BTR Polymers works side-by-side with manufacturers to create smarter material strategies.
                  Whether reclaiming in-house scrap, sourcing recycled resin, or building custom recycling
                  programs, we help companies close the loop without compromising performance or profitability.
                </div>
              </div>
            </div>

            <div style={{ ...card, display: "flex", flexDirection: "column" }}>
              <div style={{ fontWeight: 850, fontSize: 20, marginBottom: 6 }}>
                Sustainability that makes business sense
              </div>
              <div style={{ color: muted, lineHeight: 1.7 }}>
                We believe the circular economy only succeeds when it makes business sense—and that’s exactly
                where we focus.
              </div>

              <div style={{ marginTop: 14 }}>
                {[
                  "Reclaim in-house scrap streams",
                  "Source recycled resin reliably",
                  "Build custom closed-loop programs",
                  "Hit performance specs without margin pain",
                ].map((t) => (
