import { useState } from 'react';
import { NavLink, Link } from 'react-router-dom';
import { useIsMobile } from '../Core/hooks';
import {
  Navbar,
  NavbarBrand,
  NavbarToggler,
  Collapse,
  Nav,
  NavItem
} from "reactstrap";

function Navigation() {
  const [isOpen, setIsOpen] = useState(false);
  const isMobile = useIsMobile();

  const ACCENT = "#4CAF50";
  const TEXT = "#ffffff";

  const baseLinkStyle = {
    position: "relative" as const,
    color: TEXT,
    padding: "22px 16px",
    textDecoration: "none",
    display: "inline-flex",
    flexDirection: "column" as const,
    alignItems: "center",
    justifyContent: "center",
    fontWeight: 500
  };

  const underlineStyle = (active: boolean) => ({
    position: "absolute" as const,
    bottom: "8px",
    width: "60%",
    height: "2px",
    backgroundColor: ACCENT,
    opacity: active ? 1 : 0,
    transition: "opacity 0.2s ease"
  });

  return (
    <Navbar expand="xl" light className="px-3" style={{ padding: 0 }}>
      <NavbarBrand tag={Link} to="/" style={{ cursor: "pointer" }}>
        <img
          src="/images/btr-green-arrow.gif"
          alt="BTR Polymers"
          style={{ width: "75px" }}
        />
      </NavbarBrand>

      <NavbarToggler onClick={() => setIsOpen(!isOpen)} className="ms-auto">
        <span
          className="navbar-toggler-icon"
          style={{
            backgroundImage:
              "url(\"data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 30 30'%3e%3cpath stroke='white' stroke-width='2' stroke-linecap='round' d='M4 7h22M4 15h22M4 23h22'/%3e%3c/svg%3e\")"
          }}
        />
      </NavbarToggler>

      <Collapse isOpen={isOpen} navbar className="justify-content-end">
        <Nav navbar className="ms-auto">
          {[
            { to: "/solutions", label: "Solutions" },
            { to: "/materials", label: "Materials" },
            { to: "/about", label: "About" },
            { to: "/contact", label: "Contact" },
            { to: "/careers", label: "Careers" }
          ].map(({ to, label }) => (
            <NavItem key={to}>
              <NavLink
                to={to}
                onClick={() => setIsOpen(false)}
                style={({ isActive }) => ({
                  ...baseLinkStyle,
                  color: isActive ? ACCENT : TEXT
                })}
                onMouseEnter={e => {
                  const underline = e.currentTarget.querySelector(".underline") as HTMLElement;
                  if (underline) underline.style.opacity = "1";
                }}
                onMouseLeave={e => {
                  const isActive = e.currentTarget.getAttribute("aria-current");
                  const underline = e.currentTarget.querySelector(".underline") as HTMLElement;
                  if (underline && !isActive) underline.style.opacity = "0";
                }}
              >
                {label}
                <span
                  className="underline"
                  style={underlineStyle(false)}
                />
              </NavLink>
            </NavItem>
          ))}
        </Nav>
      </Collapse>
    </Navbar>
  );
}

export default Navigation;
