import React from "react";
import Navigation from "../../Components/Navigation";
import Footer from "../../Components/Footer";
import Timeline from "../../Components/Timeline";

export default function About() {
  const timeline = [
    {
      year: "2011",
      title: "Founded with an operational mindset",
      body:
        "Burdette Thomas Company LLC was founded with a simple goal: build a business grounded in quality products, strong partnerships, and disciplined execution.",
    },
    {
      year: "2012 - 2018",
      title: "Retail → national distribution platform",
      body:
        "What began as an online retailer of consumer goods-most notably backyard game products-scaled into a distribution platform serving customers across the United States.",
    },
    {
      year: "2019 - 2020",
      title: "Supply-chain focus becomes a strategic edge",
      body:
        "As the business matured, we doubled down on distribution and supply-chain solutions that supported our growth and improved reliability.",
    },
    {
      year: "2020 - 2022",
      title: "Post-COVID disruption drives expansion into recycled polymers",
      body:
        "When global shortages hit, we built creative sourcing solutions and expanded into recycled polymer markets using our distribution network and supplier relationships.",
    },
    {
      year: "2022 - Today",
      title: "Investing in circular manufacturing and vertical integration",
      body:
        "Recognizing long-term demand for sustainable materials, we expanded investment into polymer recycling operations and a vertically integrated approach from source material to end use.",
    },
    {
      year: "Next",
      title: "Scaling infrastructure, technology, and capabilities",
      body:
        "We continue investing in infrastructure, technology, and operational capabilities to support growth across additional polymer streams and end-use applications.",
    },
  ];

  return (
    <div style={{ width: "100%", background: "#FFFFFF" }}>
      <Navigation />

      {/* HERO */}
      <section
        style={{
          width: "100%",
          height: "520px",
          position: "relative",
          overflow: "hidden",
        }}
      >
        <div
          style={{
            position: "absolute",
            inset: 0,
            backgroundImage: "url(/images/about-hero.jpg)",
            backgroundSize: "cover",
            backgroundPosition: "center",
            filter: "brightness(0.92)",
          }}
        />

        <div
          style={{
            position: "absolute",
            inset: 0,
            background:
              "linear-gradient(to right, rgba(0,0,0,0.72) 0%, rgba(0,0,0,0.55) 36%, rgba(0,0,0,0.24) 70%, rgba(0,0,0,0.05) 100%)",
          }}
        />

        <div
          style={{
            position: "relative",
            height: "100%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            padding: "0 24px",
          }}
        >
          <div style={{ width: "100%", maxWidth: "1200px" }}>
            <div style={{ maxWidth: "680px" }}>
              <h1
                style={{
                  margin: 0,
                  fontSize: "clamp(40px, 5vw, 64px)",
                  lineHeight: 1.05,
                  fontWeight: 300,
                  letterSpacing: "-0.02em",
                  color: "#FFFFFF",
                  textShadow: "0 2px 8px rgba(0,0,0,0.45)",
                }}
              >
                Built Through Disruption. Focused on What Endures.
              </h1>

              <p
                style={{
                  marginTop: "18px",
                  fontSize: "clamp(18px, 2.2vw, 22px)",
                  lineHeight: 1.6,
                  fontWeight: 300,
                  color: "rgba(255,255,255,0.92)",
                  textShadow: "0 2px 8px rgba(0,0,0,0.35)",
                }}
              >
                Founded in 2011, Burdette Thomas Company evolved from a national
                distribution business into a vertically integrated recycled
                polymer partner-investing in infrastructure, market expertise,
                and sustainable manufacturing to deliver reliable outcomes for
                customers.
              </p>

              <a
                href="/solutions"
                style={{
                  display: "inline-block",
                  marginTop: "26px",
                  padding: "14px 26px",
                  background: "#003A5D",
                  color: "#FFFFFF",
                  borderRadius: "8px",
                  textDecoration: "none",
                  fontWeight: 600,
                  boxShadow: "0 10px 26px rgba(0,0,0,0.22)",
                }}
              >
                Our Solutions
              </a>
            </div>
          </div>
        </div>
      </section>

      {/* INTRO (centered, Amcor-like) */}
      <section
        style={{
          width: "100%",
          padding: "90px 24px",
          display: "flex",
          justifyContent: "center",
          background: "#FFFFFF",
        }}
      >
        <div style={{ maxWidth: "1100px", textAlign: "center" }}>
          <h2
            style={{
              margin: 0,
              fontSize: "clamp(34px, 4vw, 54px)",
              lineHeight: 1.1,
              fontWeight: 300,
              letterSpacing: "-0.02em",
              color: "#2F3A3A",
            }}
          >
            A distribution-first company evolving into circular manufacturing
          </h2>

          <p
            style={{
              margin: "22px auto 0 auto",
              maxWidth: "920px",
              fontSize: "clamp(18px, 2vw, 22px)",
              lineHeight: 1.7,
              fontWeight: 300,
              color: "#4A4A4A",
            }}
          >
            Burdette Thomas Company began as an online retailer and quickly grew
            into a scalable distribution platform across the United States. As
            global supply chains tightened, we expanded our sourcing capability
            into recycled polymers-then leaned into a bigger opportunity:
            sustainable materials and circular manufacturing solutions that
            deliver real business value.
          </p>
        </div>
      </section>

      {/* TIMELINE */}

      <Timeline />     

      {/* Timeline above */}


      {/* VALUES / HOW WE OPERATE (two column like your home) */}
      <section
        style={{
          width: "100%",
          background: "#FFFFFF",
          padding: "90px 48px",
          display: "flex",
          justifyContent: "center",
        }}
      >
        <div
          style={{
            maxWidth: "1200px",
            width: "100%",
            display: "grid",
            gridTemplateColumns: "1.1fr 0.9fr",
            gap: "80px",
            alignItems: "center",
          }}
        >
          {/* Left content */}
          <div>
            <h2
              style={{
                margin: 0,
                fontSize: "clamp(32px, 3.6vw, 46px)",
                lineHeight: 1.1,
                fontWeight: 300,
                letterSpacing: "-0.02em",
                color: "#2F3A3A",
              }}
            >
              What we believe
            </h2>

            <p
              style={{
                marginTop: "22px",
                fontSize: "clamp(18px, 2vw, 22px)",
                lineHeight: 1.7,
                fontWeight: 300,
                color: "#4A4A4A",
                maxWidth: "560px",
              }}
            >
              At our core, we are driven by long-term thinking, disciplined
              execution, and a belief that sustainable manufacturing and strong
              business fundamentals go hand in hand.
            </p>

            <p
              style={{
                marginTop: "18px",
                fontSize: "clamp(18px, 2vw, 22px)",
                lineHeight: 1.7,
                fontWeight: 300,
                color: "#4A4A4A",
                maxWidth: "560px",
              }}
            >
              We partner with customers who value reliability, transparency, and
              innovation-and we remain committed to building solutions that are
              not only good for business, but good for the future.
            </p>
          </div>

          {/* Right: big statement */}
          <div>
            <div
              style={{
                fontSize: "clamp(34px, 4vw, 56px)",
                lineHeight: 1.1,
                fontWeight: 800,
                color: "#003A5D",
              }}
            >
              Reliability.
            </div>
            <div
              style={{
                marginTop: "10px",
                fontSize: "clamp(34px, 4vw, 56px)",
                lineHeight: 1.1,
                fontWeight: 800,
                color: "#8A8A8A",
              }}
            >
              Transparency.
            </div>
            <div
              style={{
                marginTop: "10px",
                fontSize: "clamp(34px, 4vw, 56px)",
                lineHeight: 1.1,
                fontWeight: 800,
                color: "#00A3E0",
              }}
            >
              Long-Term Value.
            </div>
          </div>
        </div>

        <style>
          {`
            @media (max-width: 900px) {
              section[style*="grid-template-columns"] div {
                max-width: 100% !important;
              }
            }
          `}
        </style>
      </section>

      {/* CTA */}
      <section
        style={{
          width: "100%",
          background: "#EEF4F2",
          padding: "90px 24px",
          display: "flex",
          justifyContent: "center",
        }}
      >
        <div style={{ width: "100%", maxWidth: "1200px", textAlign: "center" }}>
          <h2
            style={{
              margin: 0,
              fontSize: "clamp(32px, 3.6vw, 46px)",
              lineHeight: 1.1,
              fontWeight: 300,
              letterSpacing: "-0.02em",
              color: "#2F3A3A",
            }}
          >
            Let’s build a durable, high-performing program
          </h2>
          <p
            style={{
              margin: "18px auto 0 auto",
              maxWidth: "860px",
              fontSize: "clamp(18px, 2vw, 22px)",
              lineHeight: 1.7,
              fontWeight: 300,
              color: "#4A4A4A",
            }}
          >
            Whether you’re sourcing recycled polymers or building a closed-loop
            approach, we bring the market insight and operational discipline to
            make it work in the real world.
          </p>

          <a
            href="/contact"
            style={{
              display: "inline-block",
              marginTop: "26px",
              padding: "14px 28px",
              background: "#003A5D",
              color: "#FFFFFF",
              borderRadius: "8px",
              textDecoration: "none",
              fontWeight: 600,
            }}
          >
            Contact Us
          </a>
        </div>
      </section>

      <Footer />
    </div>
  );
}
