import React from "react";

type TimelineItem = {
  year: string;
  title: string;
  body: string;
};

export default function Timeline({ timeline }: { timeline: TimelineItem[] }) {
  return (
    <section
      style={{
        width: "100%",
        background: "#E9EEF0",
        padding: "90px 24px",
        display: "flex",
        justifyContent: "center",
      }}
    >
      <div style={{ width: "100%", maxWidth: "1200px" }}>
        <h2
          style={{
            margin: 0,
            fontSize: "clamp(32px, 3.6vw, 46px)",
            lineHeight: 1.1,
            fontWeight: 300,
            letterSpacing: "-0.02em",
            color: "#2F3A3A",
            textAlign: "center",
          }}
        >
          Our story, by chapter
        </h2>

        <div style={{ marginTop: "48px", display: "flex", flexDirection: "column", gap: "20px" }}>
          {timeline.map((t, idx) => {
            // Pulse grows progressively but caps
            const size = Math.min(18 + idx * 4, 34);

            return (
              <div
                key={t.year + t.title}
                style={{
                  position: "relative",
                  background: "#FFFFFF",
                  borderRadius: "18px",
                  padding: "26px 26px 26px 96px", // space for pulse
                  border: "1px solid rgba(0,0,0,0.06)",
                  boxShadow: "0 10px 26px rgba(0,0,0,0.06)",
                }}
              >
                {/* Vertically centered pulse */}
                <div
                  style={{
                    position: "absolute",
                    left: "32px",
                    top: "50%",
                    transform: "translateY(-50%)",
                    width: `${size}px`,
                    height: `${size}px`,
                    borderRadius: "50%",
                    background: "#003A5D",
                    boxShadow: "0 10px 20px rgba(0,0,0,0.12)",
                  }}
                >
                  {/* pulse rings */}
                  <span
                    style={{
                      position: "absolute",
                      inset: "-10px",
                      borderRadius: "50%",
                      border: "2px solid rgba(0,58,93,0.35)",
                      animation: "btrPulse 2.2s ease-out infinite",
                    }}
                  />
                  <span
                    style={{
                      position: "absolute",
                      inset: "-18px",
                      borderRadius: "50%",
                      border: "2px solid rgba(0,58,93,0.18)",
                      animation: "btrPulse 2.2s ease-out infinite",
                      animationDelay: "0.35s",
                    }}
                  />
                </div>

                {/* Content */}
                <div>
                  <div
                    style={{
                      fontSize: "13px",
                      fontWeight: 800,
                      letterSpacing: "0.08em",
                      textTransform: "uppercase",
                      color: "#003A5D",
                      marginBottom: "6px",
                    }}
                  >
                    {t.year}
                  </div>

                  <div
                    style={{
                      fontSize: "18px",
                      fontWeight: 700,
                      color: "#003A5D",
                      letterSpacing: "-0.01em",
                    }}
                  >
                    {t.title}
                  </div>

                  <div
                    style={{
                      marginTop: "8px",
                      fontSize: "16px",
                      lineHeight: 1.6,
                      color: "#4A4A4A",
                      fontWeight: 300,
                      maxWidth: "900px",
                    }}
                  >
                    {t.body}
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        {/* Pulse animation */}
        <style>
          {`
            @keyframes btrPulse {
              0% {
                transform: scale(0.7);
                opacity: 0.6;
              }
              60% {
                transform: scale(1.05);
                opacity: 0.2;
              }
              100% {
                transform: scale(1.25);
                opacity: 0;
              }
            }
          `}
        </style>
      </div>
    </section>
  );
}
