import { useEffect, useMemo, useRef, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useIsMobile } from "../Core/hooks";

import { RxLightningBolt } from "react-icons/rx";
import { IoAnalytics, IoListSharp, IoPeople } from "react-icons/io5";
import { MdAutoStories } from "react-icons/md";
import { BiConversation } from "react-icons/bi";

import "./Navigation.css";

type MegaItem = {
  title: string;
  subtitle: string;
  path: string;
  icon: React.ReactNode;
};

function Navigation() {
  const navigate = useNavigate();
  const location = useLocation();
  const isMobile = useIsMobile();

  const [mobileOpen, setMobileOpen] = useState(false);
  const [aboutOpen, setAboutOpen] = useState(false);
  const [toolsOpen, setToolsOpen] = useState(false);

  const navRef = useRef<HTMLDivElement | null>(null);

  const logoPath = "/";

  const tools: MegaItem[] = useMemo(
    () => [
      {
        title: "Actions",
        subtitle: "Log proactive communication.",
        path: "/tools/actions",
        icon: <RxLightningBolt />,
      },
      {
        title: "Data",
        subtitle: "Measure performance.",
        path: "/tools/data",
        icon: <IoAnalytics />,
      },
      {
        title: "Lists",
        subtitle: "Save time w/ intuitive prospect and customer lists.",
        path: "/tools/lists",
        icon: <IoListSharp />,
      },
      {
        title: "Success Stories",
        subtitle: "Motivate with shared wins.",
        path: "/tools/stories",
        icon: <MdAutoStories />,
      },
      {
        title: "Meetings",
        subtitle: "Run better meetings.",
        path: "/tools/meetings",
        icon: <BiConversation />,
      },
      {
        title: "People",
        subtitle: "Support for advisors, admins, managers, and sales teams.",
        path: "/tools/people",
        icon: <IoPeople />,
      },
    ],
    []
  );

  const closeAll = () => {
    setMobileOpen(false);
    setAboutOpen(false);
    setToolsOpen(false);
  };

  // Close menus on route change
  useEffect(() => {
    closeAll();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [location.pathname]);

  // Close mobile menu if switching to desktop
  useEffect(() => {
    if (!isMobile) {
      setMobileOpen(false);
      setAboutOpen(false);
      setToolsOpen(false);
    }
  }, [isMobile]);

  // Click outside closes (mostly useful on desktop dropdowns)
  useEffect(() => {
    const onDocClick = (e: MouseEvent) => {
      if (!navRef.current) return;
      if (!navRef.current.contains(e.target as Node)) {
        setAboutOpen(false);
        setToolsOpen(false);
      }
    };
    document.addEventListener("mousedown", onDocClick);
    return () => document.removeEventListener("mousedown", onDocClick);
  }, []);

  const handleNavLink = (path: string) => {
    closeAll();
    navigate(path);
  };

  return (
    <header ref={navRef} className="sd-nav">
      <div className="sd-nav__inner">
        {/* LEFT: LOGO */}
        <Link to={logoPath} className="sd-nav__brand" aria-label="SalesDoing Home">
          <img
            className="sd-nav__logo"
            src="/images/sales-doing-logo-white-red-flame.png"
            alt="SalesDoing.com"
          />
        </Link>

        {/* RIGHT: DESKTOP LINKS */}
        <nav className="sd-nav__links" aria-label="Primary">
          {/* Tools mega */}
          <div
            className={`sd-nav__dropdown ${toolsOpen ? "is-open" : ""}`}
            onMouseEnter={() => !isMobile && setToolsOpen(true)}
            onMouseLeave={() => !isMobile && setToolsOpen(false)}
          >
            <button
              type="button"
              className="sd-nav__link sd-nav__link--dropdown"
              aria-haspopup="true"
              aria-expanded={toolsOpen}
              onClick={() => {
                if (isMobile) return;
                // optional: allow click to toggle on desktop too
                setToolsOpen((p) => !p);
                setAboutOpen(false);
              }}
            >
              Tools <span className="sd-nav__caret" />
            </button>

            <div className="sd-mega" role="menu" aria-label="Tools">
              <div className="sd-mega__grid">
                {tools.map((item) => (
                  <button
                    key={item.path}
                    type="button"
                    className="sd-mega__item"
                    onClick={() => handleNavLink(item.path)}
                  >
                    <div className="sd-mega__icon">{item.icon}</div>
                    <div className="sd-mega__text">
                      <div className="sd-mega__title">{item.title}</div>
                      <div className="sd-mega__subtitle">{item.subtitle}</div>
                    </div>
                  </button>
                ))}
              </div>
            </div>
          </div>

          <Link className="sd-nav__link" to="/integrations">
            Integrations
          </Link>

          <Link className="sd-nav__link" to="/advisors">
            Advisors
          </Link>

          {/* About dropdown */}
          <div
            className={`sd-nav__dropdown ${aboutOpen ? "is-open" : ""}`}
            onMouseEnter={() => !isMobile && setAboutOpen(true)}
            onMouseLeave={() => !isMobile && setAboutOpen(false)}
          >
            <button
              type="button"
              className="sd-nav__link sd-nav__link--dropdown"
              aria-haspopup="true"
              aria-expanded={aboutOpen}
              onClick={() => {
                if (isMobile) return;
                setAboutOpen((p) => !p);
                setToolsOpen(false);
              }}
            >
              About <span className="sd-nav__caret" />
            </button>

            <div className="sd-menu" role="menu" aria-label="About">
              <button type="button" className="sd-menu__item" onClick={() => handleNavLink("/about")}>
                About SalesDoing
              </button>
              <button
                type="button"
                className="sd-menu__item"
                onClick={() => handleNavLink("/case-studies")}
              >
                Case Studies
              </button>
              <button type="button" className="sd-menu__item" onClick={() => handleNavLink("/contact")}>
                Contact Us
              </button>
            </div>
          </div>

          <a className="sd-nav__link" href="https://salesdoing.com/blog">
            Blog
          </a>

          <Link className="sd-nav__link" to="/pricing">
            Pricing
          </Link>

          <Link className="sd-nav__link" to="/login">
            Login
          </Link>

          <button type="button" className="sd-nav__cta" onClick={() => handleNavLink("/register")}>
            START FOR FREE
          </button>
        </nav>

        {/* MOBILE: HAMBURGER */}
        <button
          type="button"
          className="sd-nav__hamburger"
          aria-label={mobileOpen ? "Close menu" : "Open menu"}
          aria-expanded={mobileOpen}
          onClick={() => {
            setMobileOpen((p) => !p);
            if (mobileOpen) {
              setAboutOpen(false);
              setToolsOpen(false);
            }
          }}
        >
          <span className="sd-nav__hamburgerLines" />
        </button>
      </div>

      {/* MOBILE: PANEL */}
      <div className={`sd-mobile ${mobileOpen ? "is-open" : ""}`}>
        <div className="sd-mobile__inner">
          {/* Tools accordion */}
          <button
            type="button"
            className="sd-mobile__row sd-mobile__row--accordion"
            aria-expanded={toolsOpen}
            onClick={() => {
              setToolsOpen((p) => !p);
              setAboutOpen(false);
            }}
          >
            <span>Tools</span>
            <span className={`sd-mobile__chev ${toolsOpen ? "is-open" : ""}`} />
          </button>

          <div className={`sd-mobile__accordion ${toolsOpen ? "is-open" : ""}`}>
            {tools.map((item) => (
              <button
                key={item.path}
                type="button"
                className="sd-mobile__subrow"
                onClick={() => handleNavLink(item.path)}
              >
                <span className="sd-mobile__subicon">{item.icon}</span>
                <span className="sd-mobile__subtext">
                  <span className="sd-mobile__subtitle">{item.title}</span>
                  <span className="sd-mobile__subhint">{item.subtitle}</span>
                </span>
              </button>
            ))}
          </div>

          <button type="button" className="sd-mobile__row" onClick={() => handleNavLink("/integrations")}>
            Integrations
          </button>

          <button type="button" className="sd-mobile__row" onClick={() => handleNavLink("/advisors")}>
            Advisors
          </button>

          {/* About accordion */}
          <button
            type="button"
            className="sd-mobile__row sd-mobile__row--accordion"
            aria-expanded={aboutOpen}
            onClick={() => {
              setAboutOpen((p) => !p);
              setToolsOpen(false);
            }}
          >
            <span>About</span>
            <span className={`sd-mobile__chev ${aboutOpen ? "is-open" : ""}`} />
          </button>

          <div className={`sd-mobile__accordion ${aboutOpen ? "is-open" : ""}`}>
            <button type="button" className="sd-mobile__subrow" onClick={() => handleNavLink("/about")}>
              About SalesDoing
            </button>
            <button type="button" className="sd-mobile__subrow" onClick={() => handleNavLink("/case-studies")}>
              Case Studies
            </button>
            <button type="button" className="sd-mobile__subrow" onClick={() => handleNavLink("/contact")}>
              Contact Us
            </button>
          </div>

          <a className="sd-mobile__row" href="https://salesdoing.com/blog" onClick={closeAll}>
            Blog
          </a>

          <button type="button" className="sd-mobile__row" onClick={() => handleNavLink("/pricing")}>
            Pricing
          </button>

          <button type="button" className="sd-mobile__row" onClick={() => handleNavLink("/login")}>
            Login
          </button>

          <button type="button" className="sd-mobile__cta" onClick={() => handleNavLink("/register")}>
            START FOR FREE
          </button>
        </div>
      </div>
    </header>
  );
}

export default Navigation;
